mapper.insert:
insert into PAGE_TEMPLET (SID, NAME, CHANNEL, 
  STATUS, C_BY, M_BY, C_TIME, 
  M_TIME, IS_DEL, TEMP_TYPE
  )
values (#{sid,jdbcType=DECIMAL}, #{name,jdbcType=OTHER}, #{channel,jdbcType=VARCHAR}, 
  #{status,jdbcType=DECIMAL}, #{cBy,jdbcType=VARCHAR}, #{mBy,jdbcType=VARCHAR}, #{cTime,jdbcType=TIMESTAMP}, 
  #{mTime,jdbcType=TIMESTAMP}, #{isDel,jdbcType=DECIMAL}, #{tempType,jdbcType=DECIMAL}
  )

mapper.select:
select SID, NAME, CHANNEL, STATUS, C_BY, M_BY, C_TIME, M_TIME, IS_DEL, TEMP_TYPE
from PAGE_TEMPLET
where SID = #{sid,jdbcType=DECIMAL}
